// N8N Code Node: Record Sell Transaction
// Inputs: $input.item.json: { sellDetails: { positionId, sellPrice }, portfolio }
// Outputs: json: { updatedPortfolio, transactionStatus, message, pnl }
console.log("ðŸ’° Record Sell Transaction Node: Processing...");
const inputData = $input.item.json;
const sellDetails = inputData?.sellDetails;
let portfolio = inputData?.portfolio;

if (!sellDetails || !sellDetails.positionId || typeof sellDetails.sellPrice !== 'number') {
  console.error("âŒ Invalid sell details input:", sellDetails);
  return { json: { updatedPortfolio: portfolio || {}, transactionStatus: 'ERROR', message: 'Critical error: Invalid sell details.' } };
}
if (!portfolio || typeof portfolio.balance !== 'number' || !Array.isArray(portfolio.openPositions) || !Array.isArray(portfolio.tradeHistory) || !portfolio.portfolioStats) {
  console.error("âŒ Invalid portfolio input:", portfolio);
  return { json: { updatedPortfolio: portfolio || {}, transactionStatus: 'ERROR', message: 'Critical error: Invalid portfolio data.' } };
}

const { positionId, sellPrice } = sellDetails;
const timestamp = new Date().toISOString();
const positionIndex = portfolio.openPositions.findIndex(p => p.id === positionId);

if (positionIndex === -1) {
  console.log(`âŒ Position with ID ${positionId} not found.`);
  return { json: { updatedPortfolio: portfolio, transactionStatus: 'ERROR', message: `Sell error: Position ID ${positionId} not found.` } };
}

const positionToSell = portfolio.openPositions[positionIndex];
const sellAmountUSD = sellPrice * positionToSell.quantity;
const pnl = sellAmountUSD - positionToSell.valueAtBuy;

portfolio.balance += sellAmountUSD;
portfolio.openPositions.splice(positionIndex, 1); 

const sellTransaction = {
  type: 'SELL', id: `sell_${positionId}`, originalPositionId: positionId, symbol: positionToSell.symbol, name: positionToSell.name,
  buyPrice: positionToSell.buyPrice, sellPrice, quantity: positionToSell.quantity,
  buyAmountUSD: positionToSell.valueAtBuy, sellAmountUSD, pnl, timestamp, status: 'CLOSED'
};
portfolio.tradeHistory.push(sellTransaction);

portfolio.portfolioStats.totalTrades += 1;
portfolio.portfolioStats.totalPnl += pnl;
if (pnl > 0) portfolio.portfolioStats.winningTrades += 1;
else if (pnl < 0) portfolio.portfolioStats.losingTrades += 1;

const closedTradesCount = portfolio.portfolioStats.winningTrades + portfolio.portfolioStats.losingTrades;
if (closedTradesCount > 0) {
  portfolio.portfolioStats.winRate = (portfolio.portfolioStats.winningTrades / closedTradesCount) * 100;
  portfolio.portfolioStats.averagePnl = portfolio.portfolioStats.totalPnl / closedTradesCount;
}

if (portfolio.balance > portfolio.portfolioStats.peakBalance) portfolio.portfolioStats.peakBalance = portfolio.balance;
if (portfolio.balance < portfolio.portfolioStats.lowestBalance) portfolio.portfolioStats.lowestBalance = portfolio.balance;

let openValue = portfolio.openPositions.reduce((sum, pos) => sum + pos.valueAtBuy, 0);
portfolio.portfolioStats.currentValue = portfolio.balance + openValue;

console.log(`âœ… SELL EXECUTED: ${positionToSell.quantity} ${positionToSell.symbol} @ ${sellPrice}. P&L: ${pnl.toFixed(2)}. New balance: ${portfolio.balance.toFixed(2)}`);
return { json: { updatedPortfolio: portfolio, transactionStatus: 'CLOSED', message: `Successfully sold ${positionToSell.quantity.toFixed(8)} ${positionToSell.symbol}. P&L: ${pnl.toFixed(2)}.`, pnl } };

