// ===== CALCUL SCALPING VIABILITY =====
function calculateScalpingViability(tokens) {
  console.log(`ðŸ§® Calculating scalping viability for ${tokens.length} tokens...`);

  return tokens.map(token => {
    let score = 0;
    let factors = [];
    let risks = [];

    // Freshness (0-25 points)
    if (token.ageMinutes < 30) {
      score += 25;
      factors.push('Ultra fresh (<30min)');
    } else if (token.ageMinutes < 60) {
      score += 20;
      factors.push('Very fresh (<1h)');
    } else if (token.ageMinutes < 120) {
      score += 15;
      factors.push('Fresh (<2h)');
    } else {
      score += 5;
      risks.push('Getting old');
    }

    // Volume (0-20 points)
    if (token.volume24h > 50000) {
      score += 20;
      factors.push('Excellent volume');
    } else if (token.volume24h > 20000) {
      score += 15;
      factors.push('Good volume');
    } else if (token.volume24h > 10000) {
      score += 10;
      factors.push('Decent volume');
    } else {
      score += 5;
      risks.push('Low volume');
    }

    // Market cap (0-20 points)
    if (token.marketCap > 10000 && token.marketCap < 100000) {
      score += 20;
      factors.push('Perfect market cap');
    } else if (token.marketCap > 5000 && token.marketCap < 150000) {
      score += 15;
      factors.push('Good market cap');
    } else if (token.marketCap < 5000) {
      score += 5;
      risks.push('Very small');
    } else {
      score -= 5;
      risks.push('Too big');
    }

    // Social (0-15 points)
    if (token.socialSignals >= 3) {
      score += 15;
      factors.push('Strong social');
    } else if (token.socialSignals >= 2) {
      score += 10;
      factors.push('Decent social');
    } else {
      score += 2;
      risks.push('Weak social');
    }

    // Platform bonuses (0-10 points)
    if (token.platform === 'pump.fun') {
      if (token.bondingProgress > 0.2 && token.bondingProgress < 0.8) {
        score += 10;
        factors.push('Active bonding');
      } else if (token.isComplete) {
        score -= 5;
        risks.push('Graduated');
      }
    }

    // Finalize
    token.scalpingViability = Math.max(0, Math.min(100, score));
    token.scalpingFactors = factors;
    token.scalpingRisks = risks;
    token.scalpingGrade = score >= 80 ? 'A' : score >= 65 ? 'B' : score >= 50 ? 'C' : 'D';

    return token;
  }).sort((a, b) => b.scalpingViability - a.scalpingViability);
}

