  const currentPrice = position.buyPrice * (1 + forcedPriceIncrease);
  const pnlPercentage = ((currentPrice - position.buyPrice) / position.buyPrice) * 100;

  console.log(`ðŸ’° FORCED Price simulation: $${currentPrice.toFixed(6)} (+${pnlPercentage.toFixed(2)}%)`);

