console.log(`âš¡ Executing ${sellSignals.length} sell(s)...`);

const executedSells = [];

for (const signal of sellSignals) {
  const position = signal.position;
  const metrics = signal.metrics;

  try {
    // Calculer la vente
    const sellAmountUSD = metrics.currentPrice * position.quantity;
    const pnl = sellAmountUSD - position.valueAtBuy;

    // Mettre Ã  jour portfolio
    portfolio.balance += sellAmountUSD;

    // Supprimer position
    const positionIndex = portfolio.openPositions.findIndex(p => p.id === position.id);
    if (positionIndex !== -1) {
      portfolio.openPositions.splice(positionIndex, 1);
    }

    // Transaction
    const sellTransaction = {
      type: 'SCALP_SELL',
      symbol: position.symbol,
      buyPrice: position.buyPrice,
      sellPrice: metrics.currentPrice,
      quantity: position.quantity,
      pnl: pnl,
      pnlPercentage: metrics.pnlPercentage,
      sellReason: signal.analysis.reason,
      timestamp: new Date().toISOString(),
      status: 'EXECUTED'
    };

    portfolio.tradeHistory.push(sellTransaction);

    // Stats
    portfolio.portfolioStats.totalTrades += 1;
    portfolio.portfolioStats.totalPnl += pnl;

    if (pnl > 0) {
      portfolio.portfolioStats.winningTrades += 1;
    } else {
      portfolio.portfolioStats.losingTrades += 1;
    }

    // Recalculer win rate
    const closedTrades = portfolio.portfolioStats.winningTrades + portfolio.portfolioStats.losingTrades;
    if (closedTrades > 0) {
      portfolio.portfolioStats.winRate = (portfolio.portfolioStats.winningTrades / closedTrades) * 100;
    }

    executedSells.push(sellTransaction);

    console.log(`âœ… SELL EXECUTED: ${position.symbol} - P&L: ${metrics.pnlPercentage.toFixed(2)}% ($${pnl.toFixed(2)})`);

  } catch (error) {
    console.error(`âŒ Failed to sell ${position.symbol}:`, error.message);
  }
}

return { 
  json: { 
    executionStatus: "EXECUTED",
    executedSells: executedSells,
    totalExecuted: executedSells.length,
    updatedPortfolio: portfolio,
    scalpSummary: {
      totalSells: executedSells.length,
      totalPnl: executedSells.reduce((sum, sell) => sum + sell.pnl, 0),
      avgPnlPercentage: executedSells.length > 0 ? 
        executedSells.reduce((sum, sell) => sum + sell.pnlPercentage, 0) / executedSells.length : 0
    },
    timestamp: new Date().toISOString()
  } 
};

