  // Analyse des patterns gagnants
  analyzeWinningPatterns() {
    const recentTrades = this.tradesHistory.slice(-50); // 50 derniers trades
    const winningTrades = recentTrades.filter(trade => (trade.pnl || 0) > 0);
    const losingTrades = recentTrades.filter(trade => (trade.pnl || 0) < 0);

    const patterns = {
      winning: {
        avgAge: this.calculateAverage(winningTrades.map(t => t.ageAtBuy || 0)),
        avgMarketCap: this.calculateAverage(winningTrades.map(t => t.marketCapAtBuy || 0)),
        avgVolume: this.calculateAverage(winningTrades.map(t => t.volumeAtBuy || 0)),
        avgSocialSignals: this.calculateAverage(winningTrades.map(t => t.socialSignalsAtBuy || 0)),
        avgHoldingTime: this.calculateAverage(winningTrades.map(t => t.holdingTimeMinutes || 0)),
        platforms: this.getMostCommon(winningTrades.map(t => t.platform)),
        timeOfDay: this.getMostCommon(winningTrades.map(t => new Date(t.timestamp).getHours()))
      },
      losing: {
        avgAge: this.calculateAverage(losingTrades.map(t => t.ageAtBuy || 0)),
        avgMarketCap: this.calculateAverage(losingTrades.map(t => t.marketCapAtBuy || 0)),
        avgVolume: this.calculateAverage(losingTrades.map(t => t.volumeAtBuy || 0)),
        avgSocialSignals: this.calculateAverage(losingTrades.map(t => t.socialSignalsAtBuy || 0))
      }
    };

    return patterns;
  }

  // GÃ©nÃ©ration de nouvelles configurations optimisÃ©es
  generateOptimizedConfig(patterns) {
    const currentConfig = {
      MIN_AGE_MINUTES: 3,
      MAX_AGE_MINUTES: 90,
      MIN_MARKET_CAP: 1500,
      MAX_MARKET_CAP: 150000,
      MIN_VOLUME_24H: 8000,
      MIN_SOCIAL_SIGNALS: 2
    };

    // Ajustements basÃ©s sur les patterns
    const optimizedConfig = { ...currentConfig };

    if (patterns.winning.avgAge > 0) {
      // Ajuster l'Ã¢ge optimal
      const optimalAge = patterns.winning.avgAge;
      optimizedConfig.MIN_AGE_MINUTES = Math.max(1, optimalAge - 10);
      optimizedConfig.MAX_AGE_MINUTES = Math.min(120, optimalAge + 20);
    }

    if (patterns.winning.avgMarketCap > 0) {
      // Ajuster la market cap optimale
      const optimalMC = patterns.winning.avgMarketCap;
      optimizedConfig.MIN_MARKET_CAP = Math.max(1000, optimalMC * 0.5);
      optimizedConfig.MAX_MARKET_CAP = Math.min(500000, optimalMC * 2);
    }

    if (patterns.winning.avgVolume > 0) {
      // Ajuster le volume minimal
      optimizedConfig.MIN_VOLUME_24H = Math.max(5000, patterns.winning.avgVolume * 0.7);
    }

    return optimizedConfig;
  }

  // Prompt IA adaptatif basÃ© sur l'apprentissage
  generateAdaptiveAIPrompt(patterns, tokenData) {
    const winRate = this.calculateWinRate();
    const bestPerformingFactors = this.identifyBestFactors(patterns);

    return `You are an ELITE MEME COIN SCALPING AI that LEARNS and ADAPTS.

ðŸ§  LEARNING DATA FROM YOUR PERFORMANCE:
- Current Win Rate: ${winRate.toFixed(1)}%
- Total Trades Analyzed: ${this.tradesHistory.length}
- Best Performing Age Range: ${patterns.winning.avgAge?.toFixed(1) || 'N/A'} minutes
- Best Market Cap Range: $${patterns.winning.avgMarketCap?.toLocaleString() || 'N/A'}
- Best Volume Range: $${patterns.winning.avgVolume?.toLocaleString() || 'N/A'}
- Most Profitable Platform: ${patterns.winning.platforms?.[0] || 'pump.fun'}
- Best Trading Hours: ${patterns.winning.timeOfDay?.[0] || 'Various'}h

ðŸŽ¯ TOP SUCCESS FACTORS (LEARNED):
${bestPerformingFactors.map(factor => `â€¢ ${factor}`).join('\\n')}

âš ï¸ FAILURE PATTERNS TO AVOID:
â€¢ Tokens aged ${patterns.losing.avgAge?.toFixed(1) || 'N/A'} minutes tend to lose
â€¢ Market caps around $${patterns.losing.avgMarketCap?.toLocaleString() || 'N/A'} underperform
â€¢ Volume below $${patterns.losing.avgVolume?.toLocaleString() || 'N/A'} is risky

ðŸ” CURRENT TOKEN TO ANALYZE:
- Symbol: ${tokenData.symbol}
- Age: ${tokenData.ageMinutes?.toFixed(1)} minutes
- Market Cap: $${tokenData.marketCap?.toLocaleString()}
- Volume: $${tokenData.volume24h?.toLocaleString()}
- Social Signals: ${tokenData.socialSignals}/4
- Platform: ${tokenData.platform}

ðŸ§  ADAPTIVE DECISION CRITERIA:
Based on your learned patterns, this token ${this.getFitScore(tokenData, patterns)} your successful pattern.

Use your learned intelligence to make a decision. You are getting smarter with each trade.

RESPOND WITH JSON:
{
  "action": "BUY" or "SKIP",
  "score": 1-10,
  "confidence": 1-100,
  "reasoning": "Explain using YOUR LEARNED PATTERNS",
  "adaptive_insights": "What patterns from your history influenced this decision",
  "learning_weight": 1-10 (how much this decision relies on learned patterns vs base criteria),
  "prediction_confidence": 1-100 (based on similarity to past successful trades)
}`;
  }

  // MÃ©thodes utilitaires
  calculateAverage(arr) {
    return arr.length > 0 ? arr.reduce((a, b) => a + b, 0) / arr.length : 0;
  }

  getMostCommon(arr) {
    const frequency = {};
    arr.forEach(item => frequency[item] = (frequency[item] || 0) + 1);
    return Object.keys(frequency).sort((a, b) => frequency[b] - frequency[a]);
  }

  calculateWinRate() {
    const recentTrades = this.tradesHistory.slice(-30);
    const wins = recentTrades.filter(trade => (trade.pnl || 0) > 0).length;
    return recentTrades.length > 0 ? (wins / recentTrades.length) * 100 : 0;
  }

  identifyBestFactors(patterns) {
    const factors = [];

    if (patterns.winning.avgAge < 30) factors.push("Ultra-fresh tokens (<30min) perform best");
    if (patterns.winning.avgMarketCap < 100000) factors.push("Mid-cap tokens are most profitable");
    if (patterns.winning.avgSocialSignals >= 2) factors.push("Social presence is crucial for success");
    if (patterns.winning.platforms?.[0]) factors.push(`${patterns.winning.platforms[0]} is your most profitable platform`);

    return factors;
  }

  getFitScore(tokenData, patterns) {
    let score = 0;
    let total = 0;

    // Compare with winning patterns
    if (patterns.winning.avgAge > 0) {
      const ageDiff = Math.abs(tokenData.ageMinutes - patterns.winning.avgAge);
      score += ageDiff < 20 ? 1 : 0;
      total += 1;
    }

    if (patterns.winning.avgMarketCap > 0) {
      const mcRatio = tokenData.marketCap / patterns.winning.avgMarketCap;
      score += (mcRatio > 0.5 && mcRatio < 2) ? 1 : 0;
      total += 1;
    }

    const fitPercentage = total > 0 ? (score / total) * 100 : 50;
    return fitPercentage > 70 ? "strongly matches" : fitPercentage > 40 ? "partially matches" : "differs from";
  }
}

