    // Facteur 3: Volume pour liquiditÃ© (0-15 points)
    const vol = this.token.volume24h || 15000;
    if (vol > 25000) {
      profitScore += 15;
      profitFactors.push(`High liquidity: $${vol.toLocaleString()}`);
