      profitScore += 12;
      profitFactors.push(`Good liquidity: $${vol.toLocaleString()}`);
      estimatedGain += 8;
    } else if (vol > 5000) {
      profitScore += 8;
      estimatedGain += 5;
    }

    // Facteur 4: Scalping Score (0-15 points)
    const scalpScore = this.token.scalpingViability || 70;
    if (scalpScore >= 80) {
      profitScore += 15;
      profitFactors.push(`Excellent scalping score: ${scalpScore}`);
