    const currentHour = new Date().getUTCHours();
    let timingScore = 30; // Score de base plus Ã©levÃ©
    let timingFactors = [];

    // Heures gÃ©nÃ©ralement profitables pour crypto (UTC)
    const profitableHours = [13, 14, 15, 16, 19, 20, 21, 22];

